class Solution {
  pair<int, int> p[10000];
public:
  int maxNumberOfFamilies(int n, vector<vector<int>>& reservedSeats) {
    int m = reservedSeats.size();
    for(int i = 0; i < m; ++i) {
      p[i] = {reservedSeats[i][0], reservedSeats[i][1]};
    }
    sort(p, p + m);
    int differentRow = 0;
    int row = -1;
    int mask = 0;
    int answer = 0;
    for(int i = 0; i < m; ++i) {
      if(p[i].first != row) {
        answer += values[mask];
        mask = INIT_MASK;
        row = p[i].first;
        differentRow += 1;
      }
      mask ^= (1 << p[i].second);
    }
    answer += values[mask];
    answer += (n - differentRow) * 2;
    return answer;
  }
};
